    useEffect(() => {
        fetchData();
      }, []);

      const fetchData = async () => {
        await axios.get("/item")
          .then((res) => res.data)
          .then((data) => setUsers(data))
      };

    return(
        <div>
            {users.map(items => (
                <li key={items._id}>
                    {items._id} - {items.name} - {items.username}
                </li>
            ))}
