import React, {useState, useEffect} from "react";
import axios from "axios";
import Edit from "./edit";

const formState = { id:null, name:'', username:'' };
const initialStae = [{ id:1, name:'Krishana', username:'krishana@gmail.com' }];

const App = () => {
    const [user, setUSer] = useState(formState);
    const [mockdata, setMockdata] = useState(initialStae);
    const [editing, setEditing] = useState(false);
    const [current, setCurrent] = useState(initialStae);

    const handleChange = (e) => {
        const {name, value} = e.target;
        setUSer({ ...user, [name]: value })
    }

    useEffect(() => {
      axios.get('/item')
          // .then(res => res.json())
          .then(res => setMockdata(res))
          .then(res => console.log('res',res))
    })



    return(
        <div>
            {mockdata.map(items => (
                <li key={items.id}>
                    {items.id}-{items.name}-{items.username}
                </li>
            ))}
        </div>
    )
}

export default App;
