import React, {useState, useEffect} from "react";
import axios from "axios";
import Edit from "./edit";

const initialState = { name:'', username:'' }
const App = () => {
    const [items, setItems] = useState(initialState);
    const [mockdata, setMockdata] = useState([]);

    useEffect(() => {
        fetchData();
      }, []);

      const fetchData = async () => {
        await axios.get("/item")
          .then((res) => res.data)
          .then((data) => setMockdata(data))
      };

      const handleChange = (e) => { 
        const {name, value} = e.target;
        setItems({...items, [name]:value})
      }

      const handleSubmit = async (e) => { 
        e.preventDefault();     
            const res = await axios.post('/item', items); 
            setItems(res)
        }

    const handleDelete = async () => { 
        await axios.delete(`/item/${props.match.params._id}`); 
        props.history.push("/"); 
    }

    return(
        <div>
            <h1>Add Items</h1>
      <form onSubmit={handleSubmit}>
          <label>Name</label>
          <input type="text" name="name" value={items.name} onChange={handleChange} />
          <input type="text" name="username" value={items.username} onChange={handleChange} />
          <input type="submit" value="Submit" />
      </form>
            {mockdata.map(items => (
                <li key={items._id}>
                    {items._id} - {items.name} - {items.username}
                </li>
            ))}
        </div>
    )
}

export default App;
