import React, {useState, useEffect} from "react";
import axios from "axios";
import Edit from "./edit";

const formState = { id:null, name:'', username:'' };
const initialStae = [{ id:1, name:'Krishana', username:'krishana@gmail.com' }];

const App = () => {
    const [user, setUSer] = useState(formState);
    const [mockdata, setMockdata] = useState(initialStae);
    const [editing, setEditing] = useState(false);
    const [current, setCurrent] = useState(initialStae);

    const handleChange = (e) => {
        const {name, value} = e.target;
        setUSer({ ...user, [name]: value })
    }

    useEffect(() => {
      axios.get('/item')
          // .then(res => res.json())
          .then(res => setMockdata(res))
    })

    const handleAdd = () => {
        user.id = mockdata.length + 1;
        setMockdata([...mockdata, user]);
        setUSer(formState);
    }

    const handleRemove = (id) => {
        setMockdata(mockdata.filter(data => data.id != id));
    }

    const handleEdit = (user) => {
        setEditing(true);
        setCurrent({ id:user.id, name:user.name, username:user.username });
    }

    const handleUpdates = (id, updates) => {
        setEditing(false);
        setMockdata(mockdata.map(items => items.id === id ? updates : items))
    }

    return(
        <div>
            {editing ? 
            <Edit 
                current = {current}
                handleUpdates = {handleUpdates}
            />
            :
            <form onSubmit={(e) => {
                e.preventDefault()
                handleAdd(user)
            }}>
                {/* <input type="text" name="name" value={user.name} onChange={handleChange} />
                <input type="text" name="username" value={user.username} onChange={handleChange} />
                <button tpe="submit">Add</button> */}
            </form>
            }
            {mockdata.map(items => (
                <li key={items.id}>
                    {items.id}-{items.name}-{items.username}
                    <button onClick={()=>handleRemove(items.id)}>X</button>
                    <button onClick={()=>handleEdit(items)}>E</button>
                </li>
            ))}
        </div>
    )
}

export default App;
