import React, { useState, useEffect } from "react"; 
import axios from 'axios'; 


const App = () => {
    const initialState = { name:'', username:'' }
    const [items, setItems] = useState(initialState) 

  const handleChange = (e) => { 
    const {name, value} = e.target;
    setItems({...items, [name]:value})
  }

  const handleSubmit = async (e) => { 
    e.preventDefault();     
        const res = await axios.post('/item', items); 
        setItems(res)
        props.history.push(`/`);  
    }

  useEffect(() => {

  })

  const handleCancel = () => {
    props.history.push("/");
  }

  return ( 
    <div>
      <h1>Add Items</h1>
      <form onSubmit={handleSubmit}>
          <label>Name</label>
          <input type="text" name="name" value={items.name} onChange={handleChange} />
          <input type="text" name="username" value={items.username} onChange={handleChange} />

          <input type="submit" value="Submit" />
          <button type="button" onClick={handleCancel} >Cancel</button>
      </form>
    </div>
  );
}

export default App;
