import React, {useState, useEffect} from "react";
import axios from "axios";
import Edit from "./edit";

const initialState = { name:'', username:'' }
const App = () => {
    const [items, setItems] = useState(initialState);
    const [mockdata, setMockdata] = useState([]);
    const [editing, setEditing] = useState(false);
    const [current, setCurrent] = useState(initialState);

    useEffect(() => {
        fetchData();
      }, []);

      const fetchData = async () => {
        await axios.get("/item")
          .then((res) => res.data)
          .then((data) => setMockdata(data))
      };

      const handleChange = (e) => { 
        const {name, value} = e.target;
        setItems({...items, [name]:value})
      }

      const handleSubmit = async (e) => { 
        e.preventDefault();     
            const res = await axios.post('/item', items); 
            setItems(res)
        }

    const handleDelete = async (_id) => { 
        await axios.delete(`/item/${_id}`); 
    }

    const handleEdit = (user) => {
        setEditing(true);
        setCurrent({ id:user.id, name:user.name, username:user.username });
    }

    const handleUpdates = async (id, updates) => {
        setEditing(false);
        const res = await axios.put(`/item/${items._id}`, items); 
        setMockdata(mockdata.map(items => items.id === id ? updates : items))
    }

    return(
        <div>
            {editing ? 
            <Edit 
                current = {current}
                handleUpdates = {handleUpdates}
            />
            :
      <form onSubmit={handleSubmit}>
          <label>Name</label>
          <input type="text" name="name" value={items.name} onChange={handleChange} />
          <input type="text" name="username" value={items.username} onChange={handleChange} />
          <input type="submit" value="Submit" />
      </form>
}
            {mockdata.map(items => (
                <li key={items._id}>
                    {items._id} - {items.name} - {items.username}<br/>
                    <button onClick={()=>handleDelete(items._id)}>X</button> 
                    <button onClick={()=>handleEdit(items)}>E</button>
                </li>
            ))}
        </div>
    )
}

export default App;
