const App = () => {
    const [user, setUSer] = useState(formState);
    const [mockdata, setMockdata] = useState(initialStae);

    useEffect(() => {
      axios.get('/item')
          // .then(res => res.json())
          .then(res => setUSer(res))
          .then(res => console.log('res',res))
    },[])



    return(
        <div>
            {mockdata.map(items => (
                <li key={items.id}>
                    {items.id}-{items.name}-{items.username}
                </li>
            ))}
        </div>
    )
}

export default App;
